#include <iostream>
using namespace std;

int main(){
    // Edit Anything Except Values
    unsigned a = 100;
    signed b = -100;
    short c = 100;
    long double d = 500.55;

    // Do Not Edit
    cout << a << "\n"; // 100
    cout << b << "\n"; // -100
    cout << c << "\n"; // 100
    cout << d << "\n"; // 500.55
}